            function changlang(lang) {
                var data={'lang':lang};
                console.log(data);
                $.get("<?php echo url('Index/changelang'); ?>",data,function(){location.reload();})
                }


        function check(){
            if($("input[name=tel]").val()==''||$("input[name=pwd]").val()==''){
                $(document).dialog({infoText: '<?php echo lang('请输入账号/密码'); ?>'});
                return false;
            }
            return true;
        }

        /*监听账号input*/
        $("input[name=tel]").bind("input propertychange", function () {
            if ($(this).val() !== '') {
                $('.icon-delete').show();
            }else{
                $('.icon-delete').hide();
            }
        });

        /*点击删除icon*/
        $(".icon-delete").on('click',function(){
            $("input[name=tel]").val('');
            $('.icon-delete').hide();
        })

        /*显示or隐藏密码*/
        $(".icon-eye").on('click',function(){
            var type = $("input[name=pwd]").attr("type");
            if(type == 'pwd'){
                $("input[name=pwd]").attr('type','text');
                return;
            }
            $("input[name=pwd]").attr('type','pwd');
        });

        /*点击登录
        $(".login").on('click',function(){*/
    function login(){
            if(check()){
                var loading = null;
                var tel   = $("input[name=tel]").val();
                var pwd   = $("input[name=pwd]").val();
                var jizhu = $("input[name=jizhu]").val();
                $.ajax({
                    url:"<?php echo url('do_login'); ?>",
                    data:{tel:tel,pwd:pwd,jizhu:jizhu},
                    type:'POST',
                    beforeSend:function(){
                        loading = $(document).dialog({
                            type : 'notice',
                            infoIcon: '/static_new/img/loading.gif',
                            infoText: '<?php echo lang('正在加载中'); ?>',
                            autoClose: 0
                        });
                    },
                    success:function(data){
                        //loading.close();
                        if(data.code==0){
                            $(document).dialog({infoText: data.info});
                            setTimeout(function(){
                                location.href="<?php echo url('index/home'); ?>"
                            },2000);
                        }else{
                            loading.close();
                            if(data.info){
                                $(document).dialog({infoText: data.info});
                            }else{
                                $(document).dialog({infoText: "<?php echo lang('网络不太稳定,请您在信号较好的地方再试'); ?>!",autoClose:2000});
                            }
                        }
                    },
                    error:function (data) {
                        loading.close();
                    }
                });
            }
        }
</script></html>